import React, { useState, useEffect, useCallback } from "react";
import UserList from "./components/UsersList";
import "./App.css";

function App() {
  const [users, setMovies] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);

  const fetchMoviesHandler = useCallback(async () => {
    setIsLoading(true);
    setError(null);
    try {
      const response = await fetch("https://localhost:7200/api/User");

      if (!response.ok) {
        throw new Error("Something went wrong!");
      }

      const data = await response.json();

      const transformedUsers = data.results.map((userData) => {
        return {
          id: userData.userId,
          name: userData.name,
          userType: userData.userType,
          email: userData.email,
          phone: userData.phone,
        };
      });
      setMovies(transformedUsers);
    } catch (error) {
      setError(error.message);
    }
    setIsLoading(false);
  }, []);

  useEffect(() => {
    fetchMoviesHandler();
  }, [fetchMoviesHandler]);

  let content = <p>Found no Users.</p>;

  if (users.length > 0) {
    content = <UserList users={users} />;
  }

  if (error) {
    content = <p>{error}</p>;
  }

  if (isLoading) {
    content = <p>Loading...</p>;
  }

  return (
    <React.Fragment>
      <section>
        <button onClick={fetchMoviesHandler}>Fetch Users</button>
      </section>
      <section>{content}</section>
    </React.Fragment>
  );
}

export default App;
